<?phpnamespace App\Http\Controllers\Roles;use App\Http\Controllers\Controller;use App\Models\Wages;use App\Services\Implementations\AnalyticService;use App\utils\DateUtils;use Illuminate\Http\Request;class WagesController extends Controller{    private Wages $model;    private AnalyticService $service;    public function __construct ()    {        $this->service = new AnalyticService();        $this->model = new Wages();    }    public function teacher (Request $request)    {        $month = $request->month ?? date('m');        $year = $request->year ?? date('Y');        $information = $this->service->getTeacherAnalytic($month, $year);        $information_on_the_month = $this->service->getTeacherAnalyticOnTheMonth($month, $year);        $weeks = DateUtils::getWeeks($month, $year);        $groups = $this->service->getGroupAnalytic($month, $year);        return view('pages.teacher.wages', [            'month' => $month,            'year' => $year,            'information' => $information,            'weeks' => $weeks,            'information_on_the_month' => $information_on_the_month,            'groups' => $groups        ]);    }}