<?phpnamespace App\Models;use Illuminate\Database\Eloquent\Factories\HasFactory;use Illuminate\Database\Eloquent\Model;use Illuminate\Http\Request;use Illuminate\Support\Facades\DB;class Admin extends Model{    use HasFactory;    public static function addAuxilPayment (Request $request)    {        DB::table("other_payments")            ->insert([                'date' => $request->date,                'comment' => $request->comment,                'price' => $request->value            ]);    }}