<?phpnamespace App\Models;use Illuminate\Database\Eloquent\Factories\HasFactory;use Illuminate\Database\Eloquent\Model;use Illuminate\Http\Request;/** * Class Premium * @package App\Models * @mixin \Illuminate\Database\Query\Builder */class Premium extends Model{    use HasFactory;    protected $table = "premiums";    public $timestamps = false;    protected $fillable = [        "date",        "value",        "teacher_id"    ];    public static function addPremium (Request $request, $id)    {        self::insert([            'date' => $request->date,            'value' => $request->value,            'teacher_id' => $id,        ]);    }}